var loopAccordion = (function($) {
  'use strict';
  var pub = {};

  /**
   * Instantiate
   */
  pub.init = function(options) {
    registerEventHandlers();
    registerBootHandlers();
  }

  /**
   * Register event handlers
   */
  function registerEventHandlers() {

    // Toggle
    $(document).on('click', '.loop-accordion-heading', function(event) {
      event.preventDefault();
      var $element = $(this);

      // Toggle active class
      $element.parents('.loop-accordion')
        .toggleClass('active');
    });
  }

  /**
   * Register boot handlers
   */
  function registerBootHandlers() {}

  return pub;
})(jQuery);

(function($) {
  'use strict';

  // Initialize
  loopAccordion.init();

})(jQuery);
